example.code:
public class OrderStatus : Enumeration
{
    public static readonly OrderStatus Pending = new(1, nameof(Pending), "Order is awaiting processing");
    public static readonly OrderStatus Processing = new(2, nameof(Processing), "Order is being processed");
    public static readonly OrderStatus Completed = new(3, nameof(Completed), "Order has been completed");

    private OrderStatus(int id, string name, string description) : base(id, name, description) { }
}
